mapper.insert:
INSERT INTO points_record (
    user_id, tournament_id, match_id, points_change, points_before, points_after,
    type, description, created_at, updated_at
) VALUES (
    #{userId}, #{tournamentId}, #{matchId}, #{pointsChange}, #{pointsBefore}, #{pointsAfter},
    #{type}, #{description}, #{createdAt}, #{updatedAt}
)

mapper.select:
SELECT * FROM points_record
WHERE tournament_id = #{tournamentId}
ORDER BY created_at DESC

SELECT * FROM points_record
WHERE user_id = #{userId}
ORDER BY created_at DESC

SELECT * FROM points_record
WHERE tournament_id = #{tournamentId}
AND user_id = #{userId}
ORDER BY created_at DESC
