mapper.select:
SELECT
FROM tournament
ORDER BY created_at DESC LIMIT #{offset}, #{query.pageSize}

mapper.insert:
INSERT INTO tournament (
    title, description, start_time, end_time, location, max_players,
    current_players, status, type, level, entry_fee, created_by,
    created_at, updated_at
)
VALUES (
    #{title}, #{description}, #{startTime}, #{endTime}, #{location},
    #{maxPlayers}, #{currentPlayers}, #{status}, #{type}, #{level},
    #{entryFee}, #{createdBy}, #{createdAt}, #{updatedAt}
)

mapper.update:
UPDATE tournament
title = #{title}, description = #{description}, start_time = #{startTime}, end_time = #{endTime}, location = #{location}, max_players = #{maxPlayers}, current_players = #{currentPlayers}, status = #{status}, type = #{type}, level = #{level}, entry_fee = #{entryFee}, updated_at = #{updatedAt}
WHERE id = #{id}
